-- Oracle initialization script for Llana
-- Converted from MySQL schema to Oracle syntax

-- Create User table
CREATE TABLE "User" (
    "ID" NUMBER GENERATED BY DEFAULT AS IDENTITY,
    "EMAIL" VARCHAR2(255) NOT NULL,
    "PASSWORD" VARCHAR2(255) NOT NULL,
    "ROLE" VARCHAR2(50) DEFAULT 'USER',
    "FIRSTNAME" VARCHAR2(255),
    "LASTNAME" VARCHAR2(255),
    "CREATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "UPDATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "DELETED_AT" TIMESTAMP,
    PRIMARY KEY ("ID"),
    CONSTRAINT "USER_EMAIL_UNIQUE" UNIQUE ("EMAIL")
);

-- Create UserApiKey table
CREATE TABLE "UserApiKey" (
    "ID" NUMBER GENERATED BY DEFAULT AS IDENTITY,
    "USER_ID" NUMBER NOT NULL,
    "API_KEY" VARCHAR2(255) NOT NULL,
    "CREATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "UPDATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "DELETED_AT" TIMESTAMP,
    PRIMARY KEY ("ID"),
    CONSTRAINT "FK_USER_API_KEY" FOREIGN KEY ("USER_ID") 
        REFERENCES "User"("ID") ON DELETE CASCADE
);

-- Create Category table
CREATE TABLE "Category" (
    "CATEGORY_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY,
    "CATEGORY_NAME" VARCHAR2(15) NOT NULL,
    "DESCRIPTION" CLOB,
    "PICTURE" BLOB,
    "CREATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "UPDATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "DELETED_AT" TIMESTAMP,
    PRIMARY KEY ("CATEGORY_ID")
);

-- Create Customer table
CREATE TABLE "Customer" (
    "CUST_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY,
    "USER_ID" NUMBER NOT NULL,
    "COMPANY_NAME" VARCHAR2(40) NOT NULL,
    "CONTACT_NAME" VARCHAR2(30),
    "CONTACT_TITLE" VARCHAR2(30),
    "ADDRESS" VARCHAR2(60),
    "CITY" VARCHAR2(15),
    "REGION" VARCHAR2(15),
    "POSTAL_CODE" VARCHAR2(10),
    "COUNTRY" VARCHAR2(15),
    "PHONE" VARCHAR2(24),
    "MOBILE" VARCHAR2(24),
    "EMAIL" VARCHAR2(225),
    "FAX" VARCHAR2(24),
    "CREATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "UPDATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "DELETED_AT" TIMESTAMP,
    PRIMARY KEY ("CUST_ID"),
    CONSTRAINT "FK_CUSTOMER_USER" FOREIGN KEY ("USER_ID") 
        REFERENCES "User"("ID") ON DELETE CASCADE
);

-- Create Employee table
CREATE TABLE "Employee" (
    "EMPLOYEE_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY,
    "LASTNAME" VARCHAR2(20) NOT NULL,
    "FIRSTNAME" VARCHAR2(10) NOT NULL,
    "TITLE" VARCHAR2(30),
    "TITLE_OF_COURTESY" VARCHAR2(25),
    "BIRTHDATE" TIMESTAMP,
    "HIREDATE" TIMESTAMP,
    "ADDRESS" VARCHAR2(60),
    "CITY" VARCHAR2(15),
    "REGION" VARCHAR2(15),
    "POSTAL_CODE" VARCHAR2(10),
    "COUNTRY" VARCHAR2(15),
    "PHONE" VARCHAR2(24),
    "EXTENSION" VARCHAR2(4),
    "MOBILE" VARCHAR2(24),
    "EMAIL" VARCHAR2(225),
    "PHOTO" BLOB,
    "NOTES" CLOB,
    "PHOTO_PATH" VARCHAR2(255),
    "MGR_ID" NUMBER,
    "CREATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "UPDATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "DELETED_AT" TIMESTAMP,
    PRIMARY KEY ("EMPLOYEE_ID")
);

-- Create Shipper table
CREATE TABLE "Shipper" (
    "SHIPPER_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY,
    "COMPANY_NAME" VARCHAR2(40) NOT NULL,
    "PHONE" VARCHAR2(44),
    "CREATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "UPDATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "DELETED_AT" TIMESTAMP,
    PRIMARY KEY ("SHIPPER_ID")
);

-- Create SalesOrder table
CREATE TABLE "SalesOrder" (
    "ORDER_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY,
    "CUST_ID" NUMBER NOT NULL,
    "EMPLOYEE_ID" NUMBER,
    "ORDER_DATE" TIMESTAMP,
    "REQUIRED_DATE" TIMESTAMP,
    "SHIPPED_DATE" TIMESTAMP,
    "SHIPPER_ID" NUMBER NOT NULL,
    "FREIGHT" NUMBER(10,2),
    "SHIP_NAME" VARCHAR2(40),
    "SHIP_ADDRESS" VARCHAR2(60),
    "SHIP_CITY" VARCHAR2(15),
    "SHIP_REGION" VARCHAR2(15),
    "SHIP_POSTAL_CODE" VARCHAR2(10),
    "SHIP_COUNTRY" VARCHAR2(15),
    "CREATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "UPDATED_AT" TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    "DELETED_AT" TIMESTAMP,
    PRIMARY KEY ("ORDER_ID"),
    CONSTRAINT "FK_SALES_SHIPPER" FOREIGN KEY ("SHIPPER_ID")
        REFERENCES "Shipper"("SHIPPER_ID"),
    CONSTRAINT "FK_SALES_CUSTOMER" FOREIGN KEY ("CUST_ID")
        REFERENCES "Customer"("CUST_ID")
);

-- Create triggers for UPDATED_AT timestamps
CREATE OR REPLACE TRIGGER "User_UPDATE_AT"
BEFORE UPDATE ON "User"
FOR EACH ROW
BEGIN
    :NEW."UPDATED_AT" := CURRENT_TIMESTAMP;
END;
/

